import{_ as s,c as e,o as n,a}from"./app.a8a670f6.js";const h=JSON.parse('{"title":"Introduction","description":"","frontmatter":{"title":"Introduction"},"headers":[{"level":2,"title":"What is Xeito?","slug":"what-is-xeito","link":"#what-is-xeito","children":[]},{"level":2,"title":"Components","slug":"components","link":"#components","children":[]},{"level":2,"title":"Services","slug":"services","link":"#services","children":[]},{"level":2,"title":"Tagged Template Literals","slug":"tagged-template-literals","link":"#tagged-template-literals","children":[]},{"level":2,"title":"Ready for more?","slug":"ready-for-more","link":"#ready-for-more","children":[]}],"relativePath":"guide/introduction.md"}'),o={name:"guide/introduction.md"},t=a(`<h1 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-hidden="true">#</a></h1><div class="warning custom-block"><p class="custom-block-title">ACTIVE DEVELOPMENT</p><p><strong>Xeito</strong> is still under active development, therefore everything is still <strong>unstable</strong> and untested. API may introduce <strong>breaking changes</strong> between minor versions without notice.</p></div><h2 id="what-is-xeito" tabindex="-1">What is Xeito? <a class="header-anchor" href="#what-is-xeito" aria-hidden="true">#</a></h2><p>Xeito (pronounced /ˈʃejto̝/, <a href="https://en.wiktionary.org/wiki/xeito" target="_blank" rel="noreferrer">from Galician</a>: &quot;way&quot;, &quot;manner&quot; or &quot;fashion&quot;) is a <a href="https://www.typescriptlang.org/" target="_blank" rel="noreferrer">Typescript</a> framework for building web applications.</p><p>It builds on top of the powerful features of Typescript and <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Template_literals" target="_blank" rel="noreferrer">Tagged Template Literals</a>, providing a component-based programming model that helps you develop user applications, no matter the complexity.</p><p>Let&#39;s see an example of a barebones Xeito application:</p><div class="language-typescript"><button title="Copy Code" class="copy"></button><span class="lang">typescript</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;">// main.ts</span></span>
<span class="line"><span style="color:#89DDFF;font-style:italic;">import</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">Xeito</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;font-style:italic;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">@xeito/core</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;font-style:italic;">import</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">AppComponent</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;font-style:italic;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">./app-component</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> app </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">new</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">Xeito</span><span style="color:#A6ACCD;">(AppComponent)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">app</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">boostrap</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">#app</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span></code></pre></div><div class="language-typescript"><button title="Copy Code" class="copy"></button><span class="lang">typescript</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;">// app-component.ts</span></span>
<span class="line"><span style="color:#89DDFF;font-style:italic;">import</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">Component</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">XeitoComponent</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">State</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">html</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;font-style:italic;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">@xeito/core</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#89DDFF;">@</span><span style="color:#82AAFF;">Component</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#F07178;">selector</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">app-root</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#89DDFF;font-style:italic;">export</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">class</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">Counter</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">extends</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">XeitoComponent</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">@</span><span style="color:#82AAFF;">State</span><span style="color:#A6ACCD;">() </span><span style="color:#F07178;">count</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">number</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#F07178;">increment</span><span style="color:#89DDFF;">()</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;">this.</span><span style="color:#A6ACCD;">count</span><span style="color:#89DDFF;">++;</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#F07178;">render</span><span style="color:#89DDFF;">()</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;font-style:italic;">return</span><span style="color:#F07178;"> </span><span style="color:#82AAFF;">html</span><span style="color:#89DDFF;">\`</span></span>
<span class="line"><span style="color:#C3E88D;">      &lt;div&gt;</span></span>
<span class="line"><span style="color:#C3E88D;">        &lt;button @click=</span><span style="color:#89DDFF;">\${</span><span style="color:#89DDFF;">this.</span><span style="color:#A6ACCD;">increment</span><span style="color:#89DDFF;">}</span><span style="color:#C3E88D;">&gt;</span></span>
<span class="line"><span style="color:#C3E88D;">          Count is: </span><span style="color:#89DDFF;">\${</span><span style="color:#89DDFF;">this.</span><span style="color:#A6ACCD;">count</span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#C3E88D;">        &lt;/button&gt;</span></span>
<span class="line"><span style="color:#C3E88D;">      &lt;/div&gt;</span></span>
<span class="line"><span style="color:#C3E88D;">    </span><span style="color:#89DDFF;">\`</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><p>Above we can see two basic features of Xeito:</p><ul><li><p><strong>Tagged Template Literal</strong>: Xeito uses <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Template_literals" target="_blank" rel="noreferrer">Tagged Template Literals</a> to compose the template inside the render method of a component and it allows you to declaratively create the DOM tree of your component.</p></li><li><p><strong>Reactivity</strong>: Xeito tracks the state of your components and updates the DOM when it changes. This is done by using the <code>@State()</code> and <code>@Prop()</code> decorators (more on that later).</p></li></ul><div class="tip custom-block"><p class="custom-block-title">PREREQUISITES</p><p>The documentation assumes familiarity with HTML, CSS and Typescript. Please, if you are unsure if your knowledge level will suffice, check it at <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Language_Overview" target="_blank" rel="noreferrer">Mozilla Language Overview</a> and the <a href="https://www.typescriptlang.org/docs/handbook/intro.html" target="_blank" rel="noreferrer">Typescript Handbook</a>. Prior experience with other frameworks like React will help, but it isn&#39;t a hard requirement.</p></div><h2 id="components" tabindex="-1">Components <a class="header-anchor" href="#components" aria-hidden="true">#</a></h2><p>Components are the building blocks you will use to compose an application using Xeito.</p><p>A component is just a Typescript class extending <code>XeitoComponent</code> with a <code>@Component()</code> decorator, a <code>render()</code> method that returns the template and reactive properties decorated with <code>@State()</code> or <code>@Prop()</code>.</p><p>Xeito registers your components inside the <a href="https://developer.mozilla.org/en-US/docs/Web/API/CustomElementRegistry" target="_blank" rel="noreferrer">custom elements registry</a>, so you can use them in your HTML templates. Even if Xeito leverages the web components standard, you don&#39;t need to know anything about it to use Xeito and its components are not designed to be used outside of the framework.</p><h2 id="services" tabindex="-1">Services <a class="header-anchor" href="#services" aria-hidden="true">#</a></h2><p>Services are a form of <strong>Dependency Injection</strong>. They allow you to have dependencies without taking care of their initialization, Xeito takes care of that for you.</p><p>In its most basic form a service is a data provider, it keeps the logic to access the data it contains and, since they&#39;re only instantiated once, can be shared between multiple components as a global state container. The main objective of the services is to organize your logic, data and functions and share it among multiple components if needed. Just like the <strong>Components</strong>, a Xeito service is just a Typescript class with a <code>@Injectable()</code> decorator that will tell Xeito it needs to instantiate it and make it accessible for the components.</p><p>After a service is created, it can be injected into a component by adding a property with the <code>@Inject()</code> decorator.</p><p>This can sound confusing, but in reality it&#39;s very simple to implement. Let&#39;s see an example:</p><p>We start by creating a new service like this:</p><div class="language-typescript"><button title="Copy Code" class="copy"></button><span class="lang">typescript</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;">// my-service.ts</span></span>
<span class="line"><span style="color:#89DDFF;">@</span><span style="color:#82AAFF;">Injectable</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#F07178;">selector</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">greeterService</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#89DDFF;font-style:italic;">export</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">class</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">GreeterService</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#F07178;">greet</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;font-style:italic;">name</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">string</span><span style="color:#89DDFF;">)</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#A6ACCD;">console</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">log</span><span style="color:#F07178;">(</span><span style="color:#89DDFF;">\`</span><span style="color:#C3E88D;">Hello </span><span style="color:#89DDFF;">\${</span><span style="color:#A6ACCD;">name</span><span style="color:#89DDFF;">}</span><span style="color:#C3E88D;">!</span><span style="color:#89DDFF;">\`</span><span style="color:#F07178;">)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span>
<span class="line"></span></code></pre></div><p>And then we <em>inject</em> it into a component</p><div class="language-typescript"><button title="Copy Code" class="copy"></button><span class="lang">typescript</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;">// my-component.ts</span></span>
<span class="line"><span style="color:#89DDFF;">@</span><span style="color:#82AAFF;">Component</span><span style="color:#A6ACCD;">()</span></span>
<span class="line"><span style="color:#89DDFF;font-style:italic;">export</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">class</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">MyComponent</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">@</span><span style="color:#82AAFF;">Inject</span><span style="color:#A6ACCD;">() </span><span style="color:#F07178;">greeterService</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">GreeterService</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#C792EA;">constructor</span><span style="color:#89DDFF;">()</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#A6ACCD;">super</span><span style="color:#F07178;">()</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#676E95;font-style:italic;">// Now we can call the methods of the service</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;">this.</span><span style="color:#A6ACCD;">greeterService</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">greet</span><span style="color:#F07178;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">World</span><span style="color:#89DDFF;">&#39;</span><span style="color:#F07178;">)</span><span style="color:#89DDFF;">;</span><span style="color:#F07178;"> </span><span style="color:#676E95;font-style:italic;">// Logs &#39;Hello World!&#39;</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span>
<span class="line"></span></code></pre></div><h4 id="what-s-happening-here" tabindex="-1">What&#39;s happening here? <a class="header-anchor" href="#what-s-happening-here" aria-hidden="true">#</a></h4><p>It may look complicated but it&#39;s actually not, let&#39;s take a look at the first class: <code>GreeterService</code> as we mentioned before is decorated with the <code>@Injectable()</code> decorator. This tells Xeito it needs to keep an instance of it to be accessible by the components.</p><p>The specified <code>selector</code> key tells the framework what name it has so it can be injected by a component later.</p><p>After that, we decorate a property of <code>MyComponent</code> with a <code>@Inject()</code> decorator. The property name will be used to find the service we want to inject (the one with the <code>selector</code> key we specified before). We can also specify a name in the decorator like this: <code>@Inject(&#39;greeterService&#39;)</code> if we want to use a different name for the property.</p><p>Now our component has access to all the public methods and properties of the service and can call them whenever it needs to. Since the service is a <em><strong>singleton</strong></em> (there is only one instance of it for the entire app), its inner state is shared no matter how many components use it. We can use this to create global state containers, cache http responses or any other kind of data that needs to be shared between components.</p><h2 id="tagged-template-literals" tabindex="-1">Tagged Template Literals <a class="header-anchor" href="#tagged-template-literals" aria-hidden="true">#</a></h2><p>You might have noticed our components are not importing some HTML template, instead they have a <code>render()</code> method that returns a tagged template string, the contents of the string are a declarative HTML string:</p><div class="language-ts"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#82AAFF;">html</span><span style="color:#89DDFF;">\`</span><span style="color:#C3E88D;">&lt;div&gt;Hello </span><span style="color:#89DDFF;">\${</span><span style="color:#A6ACCD;">name</span><span style="color:#89DDFF;">}</span><span style="color:#C3E88D;">!&lt;/div&gt;</span><span style="color:#89DDFF;">\`</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span></code></pre></div><p>Briefly explained, under the hood Xeito uses a library called <a href="https://github.com/webreflection/uhtml" target="_blank" rel="noreferrer">µhtml</a>. This library provides a set of utilities to create and update the DOM content based on this template literals, to be able to do this they are tagged with the <code>html</code> function. Tagging template literals this way allows the function to receive the an array of strings and an array of the expressions interpolated within, µhtml uses this to create the DOM tree and update only the parts that change.</p><h2 id="ready-for-more" tabindex="-1">Ready for more? <a class="header-anchor" href="#ready-for-more" aria-hidden="true">#</a></h2><p>We&#39;ve briefly introduced some of the main features of Xeito&#39;s core. The rest of the documentation will cover these two and other more advanced features in much finer detail.</p>`,35),l=[t];function p(r,c,i,y,D,d){return n(),e("div",null,l)}const C=s(o,[["render",p]]);export{h as __pageData,C as default};
